import React, { useEffect, useState } from "react";
import InfoCard from "./InfoCard";

const date = new Date();
const day = date.getDay();
const month = date.getMonth() - 1;

const Data = (props) => {
  const [cards, setCards] = useState([
    {
      year: "-45",
      month: "01",
      day: "01",
      event:
        "The Julian calendar takes effect as the civil calendar of the Roman Empire, establishing January 1 as the new date of the new year.",
    },
    {
      year: "366",
      month: "01",
      day: "02",
      event:
        "The Alemanni cross the frozen Rhine in large numbers, invading the Roman Empire.",
    },
    {
      year: "250",
      month: "01",
      day: "03",
      event:
        "Emperor Decius orders everyone in the Roman Empire (except Jews) to make sacrifices to the Roman gods.",
    },
    {
      year: "98",
      month: "01",
      day: "27",
      event:
        "Trajan succeeds his adoptive father Nerva as Roman emperor; under his rule the Roman Empire will reach its maximum extent.",
    },
    {
      year: "1018",
      month: "01",
      day: "30",
      event: "Poland and the Holy Roman Empire conclude the Peace of Bautzen.",
    },
    {
      year: "421",
      month: "02",
      day: "08",
      event: "Constantius III becomes co-Emperor of the Western Roman Empire.",
    },
  ]);

  useEffect(() => {
    fetch(
      `https://api.api-ninjas.com/v1/historicalevents?month=${month}&day=${day}`,
      {
        method: "GET",
        headers: { "X-Api-Key": "Fc5jLn8ODsaHw0Sn/a9pMQ==TvVWzRhLKy5EAehX" },
        contentType: "application/json",
      }
    )
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          console.log(error);
        }
      );
  }, []);
  return (
    <div className="dataWrapper" id="dataWrapper">
      {cards.map((card) => (
        <InfoCard today={props.today} year={card.year} event={card.event} />
      ))}
    </div>
  );
};

export default Data;
