import React, { useState, useEffect } from "react";

const removeMarkdown = require("remove-markdown");

const Body = () => {
  const [fishList, setFish] = useState("");

  const randomFish = () => {
    fetch("https://www.fishwatch.gov/api/species")
      .then((res) => res.json())
      .then(
        (everyFish) => {
          setFish(
            removeMarkdown(
              everyFish[Math.floor(Math.random() * 115)]["Species Name"]
            )
          );
        },
        (error) => {
          console.log("error");
        }
      );
  };

  return (
    <div className="title">
      <h1>{fishList}</h1>
      <button type="button" className="btn btn-dark" onClick={randomFish}>
        Get Random Fish!
      </button>
    </div>
  );
};

export default Body;
