import React, { useState, useEffect } from "react";

const removeMarkdown = require("remove-markdown");

const Body = () => {
  const [fishList, setFishList] = useState("");
  const [fishImg, setImg] = useState("");

  const randomFish = () => {
    setImg("https://media0.giphy.com/media/3oEjI6SIIHBdRxXI40/200.gif");
    fetch("https://www.fishwatch.gov/api/species")
      .then((res) => res.json())
      .then(
        (everyFish) => {
          setFishList(everyFish[Math.floor(Math.random() * 115)]);
          setImg(fishList["Image Gallery"][0]["src"]);
        },
        (error) => {
          console.log("error");
        }
      );
  };

  return (
    <div className="title">
      <h1>{removeMarkdown(fishList["Species Name"])}</h1>
      <img className="img" src={fishImg} alt="fish" />
      <button type="button" className="btn btn-dark" onClick={randomFish}>
        Get Random Fish!
      </button>
    </div>
  );
};

export default Body;
