  };

  return (
    <div className="mainWrapper">
      <div className="titleContainer">
        <p className="today">{today}</p>
        <h1 className="title">On This Day...</h1>
        <p>by John von Muhlen</p>
      </div>
      <div className="buttonContaier">
        <button
          onClick={scroll}
          type="button"
          className="btn btn-outline-secondary"
        >
          Show me
        </button>
      </div>
    </div>
  );
};

export default Body;
